import React, {
  createContext,
  useCallback,
  useContext,
  useReducer,
} from "react";

const initState = {
  labels: ["test"],
  allItems: [],
  // const newItem = {
  //   id: allItems.length + 1,
  //   label: category,
  //   value: value,
  //   color: random_rgba(), //"#" + Math.floor(Math.random() * 16777215).toString(16),
  // };
};

function reducer(state, action) {
  switch (action.type) {
    case "ADD":
      return {
        ...state,
        labels: [...state.labels, action.value],
      };
    case "ADD_ITEM":
      console.log(action.value);
      const newItem = action.value;
      const allItems = state.allItems;

      const itemAlreadyExist = allItems.find(
        (item) => item.category === newItem.label
      );
      if (itemAlreadyExist) {
        itemAlreadyExist.items.push(newItem)
        i.value += newItem.value;
        newItem.color = itemAlreadyExist.color;
        newItem.id = itemAlreadyExist.id;

        const updatedItems = allItems.filter(
          (item) => item.label !== newItem.label
        );
        return {
          ...state,
          allItems: [...updatedItems, newItem],
        };
      }
      return {
        ...state,

        allItems: [...state.allItems, action.value],
      };

    default:
      return state;
  }
}

const initialContext = {
  state: initState,
  dispatch: () => {},
};

const MainContext = createContext(initialContext);

export function MainProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initState, undefined);

  return (
    <MainContext.Provider value={{ state, dispatch }}>
      {children}
    </MainContext.Provider>
  );
}

export const useMainState = () => {
  const { state, dispatch } = useContext(MainContext);

  const setLabels = useCallback(
    (value) => {
      dispatch({ type: "ADD", value });
    },
    [dispatch]
  );
  return { labels: state.labels, setLabels };
};

export const useAllItemsState = () => {
  const { state, dispatch } = useContext(MainContext);

  const setAllItems = useCallback(
    (value) => {
      dispatch({ type: "ADD_ITEM", value });
    },
    [dispatch]
  );
  return { allItems: state.allItems, setAllItems };
};
