import React, {
  useState,
  useContext,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import { useAllItemsState, useMainState } from "../reducers/mainReducer";
import MonthlyPie from "./MonthlyPie";
import MonthlyForm from "./MonthlyForm";
import {
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@material-ui/core";
import { categories } from "../constants/form";

import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";

const Row = (props) =>{

    return (<TableRow key={category.label}>
              <TableCell>
                {" "}
                <IconButton
                  aria-label="expand row"
                  size="small"
                  onClick={() => setOpen(!open)}
                >
                  {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                </IconButton>
              </TableCell>
              <TableCell component="th" scope="row">
                {category.label}
              </TableCell>
              <TableCell align="right">
                {rows.find((o) => o.category === category.label) ? (
                  <div>
                    {" "}
                    {formatter.format(
                      rows.find((o) => o.category === category.label).value
                    )}{" "}
                  </div>
                ) : (
                  "N/A"
                )}
              </TableCell>
            </TableRow>)
}
function MonthlyTable({ data }) {
  const { allItems } = useAllItemsState();
  const [enteringData, setEnteringData] = useState(true);
  const [rows, setRows] = useState([]);
  const [open, setOpen] = useState();

  const createData = (category, value) => {
    return { category, value };
  };

  useEffect(() => {
    const processedRows = allItems.map((item) =>
      createData(item.label, item.value)
    );
    console.log(processedRows);
    setRows(processedRows);
  }, [data]);

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
  });
  console.log("here", rows);

  return (
    <TableContainer>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell></TableCell>
            <TableCell>Category</TableCell>
            <TableCell align="right">Value&nbsp;($)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {categories.map((category, idx) => (
            <Row props={category}/>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
export default MonthlyTable;
