import {
  Button,
  CircularProgress,
  FormGroup,
  Input,
  InputAdornment,
  MenuItem,
  TextField,
} from "@material-ui/core";
import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { categories } from "../constants/form";
import { useDispatch } from "react-redux";
import CurrencyTextField from "@unicef/material-ui-currency-textfield";

import { useAllItemsState, useMainState } from "../reducers/mainReducer";

function MonthlyForm() {
  const [category, setCategory] = useState("");
  const [value, setValue] = useState();
  const [enteringData, setEnteringData] = useState(true);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState();
  const [error, setError] = useState();
const [description, setDescription] = useState("")
  const { labels, setLabels } = useMainState();
  const { allItems, setAllItems } = useAllItemsState();
  useEffect(() => {
    setList(labels);
  }, [labels]);

  const reset = () => {
    setCategory("");
    setValue("");
    setEnteringData(true);
    setLoading(false);
  };
  const random_rgba = () => {
    var o = Math.round,
      r = Math.random,
      s = 255;
    return (
      "rgba(" +
      o(r() * s) +
      "," +
      o(r() * s) +
      "," +
      o(r() * s) +
      "," +
      r().toFixed(1) +
      ")"
    );
  };

  const handleAdd = () => {
    setError("");
    // setLoading(true);
    setEnteringData(false);
    if (category && value) {
      const newItem = {
        id: allItems.length + 1,
        label: category,
        value: Number(value),
        color: random_rgba(), //"#" + Math.floor(Math.random() * 16777215).toString(16),
        desc
      };
      setAllItems(newItem);
      reset();
    } else {
      setError("error");
    }
  };
  return loading ? (
    <CircularProgress />
  ) : (
    <FormGroup className="form_control">
        <div>


      <TextField
        className="form_control__input"
        id="standard-select-category"
        select
        label="Select"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        helperText="Please select your category"
      >
        {categories.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      </div>
      <div>
      <TextField
          id="standard-multiline-flexible"
          label="Multiline"
          multiline
          maxRows={4}
          value={value}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div>
        <CurrencyTextField
          className="form_control--input"
          label="Amount"
          variant="standard"
          value={value}
          currencySymbol="$"
          minimumValue="0"
          outputFormat="string"
          decimalCharacter="."
          digitGroupSeparator=","
          onChange={(e, value) => setValue(value)}
        />
      </div>

      <Button
        variant="contained"
        color="primary"
        type="submit"
        onClick={(e) => handleAdd()}
      >
        Submit
      </Button>
      <div> {error}</div>
    </FormGroup>
  );
}

export default MonthlyForm;
