import React, {
  useState,
  useContext,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import { useAllItemsState, useMainState } from "../reducers/mainReducer";
import MonthlyPie from "./MonthlyPie";
import MonthlyForm from "./MonthlyForm";
import {
  Box,
  Collapse,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
} from "@material-ui/core";
import { categories } from "../constants/form";

import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";
import { formatter } from "../utils/index";
const Row = ({ category, rows }) => {
  const [open, setOpen] = useState(false);
  console.log(category);
  const obj = rows.find((o) => o.category === category.label);
  return (
    <>
      <TableRow key={category.label}>
        <TableCell>
          {" "}
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {category.label}
        </TableCell>
        <TableCell align="right">
          {obj ? <div>{formatter.format(obj.value)}</div> : "N/A"}
        </TableCell>
      </TableRow>
      <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
        <Collapse in={open} timeout="auto" unmountOnExit>
          <Box margin={1}>
            <div>History</div>
            <Table size="small" aria-label="purchases">
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell>Description</TableCell>
                  <TableCell align="right">Amount</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {category.map((historyRow) => (
                  <TableRow key={historyRow.id}>
                    <TableCell component="th" scope="row">
                      {historyRow ? historyRow.date : ""}
                    </TableCell>
                    <TableCell align="right">
                      {historyRow.description}
                    </TableCell>
                    <TableCell align="right">
                      {historyRow && historyRow.value}
                    </TableCell>
                    <TableCell align="right"></TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        </Collapse>
      </TableCell>
    </>
  );
};
function MonthlyTable({ data }) {
  const { allItems } = useAllItemsState();
  const [enteringData, setEnteringData] = useState(true);
  const [rows, setRows] = useState([]);

  const createData = (category, value) => {
    return { category, value };
  };

  useEffect(() => {
    const processedRows = allItems.map((item) =>
      createData(item.category, item.totalValue)
    );
    console.log(processedRows);
    setRows(processedRows);
  }, [data]);

  return (
    <TableContainer>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell></TableCell>
            <TableCell>Category</TableCell>
            <TableCell align="right">Value&nbsp;($)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {categories.map((category, idx) => (
            <Row category={category} rows={rows} />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
export default MonthlyTable;
